Bash("/c/Windows/System32/WindowsPowerShell/v1.0/powershell.exe" -Command "
Add-Type -AssemblyName System.Security
$cred = [System.Net.CredentialCache]::DefaultNetworkCredentials
# Use the Windows Credential Manager API
Add-Type @''
using System;
using System.Runtime.InteropServices;
using System.Text;

public class CredManager {
    [DllImport\(""advapi32.dll"", SetLastError = true, CharSet = CharSet.Unicode\)]
    public static extern bool CredRead\(string target, int type, int flags, out IntPtr credential\);

    [DllImport\(""advapi32.dll""\)]
    public static extern void CredFree\(IntPtr credential\);

    [StructLayout\(LayoutKind.Sequential, CharSet = CharSet.Unicode\)]
    public struct CREDENTIAL {
        public int Flags;
        public int Type;
        public string TargetName;
        public string Comment;
        public System.Runtime.InteropServices.ComTypes.FILETIME LastWritten;
        public int CredentialBlobSize;
        public IntPtr CredentialBlob;
        public int Persist;
        public int AttributeCount;
        public IntPtr Attributes;
        public string TargetAlias;
        public string UserName;
    }

    public static string GetCredential\(string target\) {
        IntPtr credPtr;
        if \(CredRead\(target, 1, 0, out credPtr\)\) {
            CREDENTIAL cred = \(CREDENTIAL\)Marshal.PtrToStructure\(credPtr, typeof\(CREDENTIAL\)\);
            string password = Marshal.PtrToStringUni\(cred.CredentialBlob, cred.CredentialBlobSize / 2\);
            CredFree\(credPtr\);
            return password;
        }
        return null;
    }
}
''@

$token = [CredManager]::GetCredential\(''Supabase CLI:supabase''\)
Write-Output $token
")

Bash("/c/Windows/System32/WindowsPowerShell/v1.0/powershell.exe" -Command "
Add-Type @''
using System;
using System.Runtime.InteropServices;

public class CredManager2 {
    [DllImport\(""advapi32.dll"", SetLastError = true, CharSet = CharSet.Unicode\)]
    public static extern bool CredRead\(string target, int type, int flags, out IntPtr credential\);

    [DllImport\(""advapi32.dll""\)]
    public static extern void CredFree\(IntPtr credential\);

    [StructLayout\(LayoutKind.Sequential, CharSet = CharSet.Unicode\)]
    public struct CREDENTIAL {
        public int Flags;
        public int Type;
        public string TargetName;
        public string Comment;
        public long LastWritten;
        public int CredentialBlobSize;
        public IntPtr CredentialBlob;
        public int Persist;
        public int AttributeCount;
        public IntPtr Attributes;
        public string TargetAlias;
        public string UserName;
    }

    public static string GetCredential\(string target\) {
        IntPtr credPtr;
        if \(CredRead\(target, 1, 0, out credPtr\)\) {
            CREDENTIAL cred = \(CREDENTIAL\)Marshal.PtrToStructure\(credPtr, typeof\(CREDENTIAL\)\);
            byte[] bytes = new byte[cred.CredentialBlobSize];
            Marshal.Copy\(cred.CredentialBlob, bytes, 0, cred.CredentialBlobSize\);
            CredFree\(credPtr\);
            return System.Text.Encoding.UTF8.GetString\(bytes\);
        }
        return ""NOT_FOUND"";
    }
}
''@

$token = [CredManager2]::GetCredential\(''Supabase CLI:supabase''\)
Write-Output $token
")
